// es7 snippets
import React from 'react'
import { Card, CardContent, Typography } from '@material-ui/core';
import './Message.css'

function Message({message, username }) {
    const isUsername = username === message.username;

    return (
        <div>
            <Card className={`message ${isUser && 'message__user'}`}>
                <CardContent>
                    <Typography 
                        variant="h5"
                        color="white"
                        component="h2"
                    >
                        {username}: {message}
                    </Typography>
                </CardContent>
            </Card>
        </div>
    )
}

export default Message
